public class CharDemo {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		char a1, b1, c1=0;
		a1 = 'X';
		b1 = 88;
		System.out.println("Char a1+b1= "+ a1+b1);
		//Вивід символів char. 127 ANSII
		for (int i = 0; i < 255; i++) {
			System.out.print(c1);
			c1++;
		}		
	}

}
